(window["webpackJsonp"]=window["webpackJsonp"]||[]).push([["about"],{"613f":function(t,n,e){"use strict";e.r(n);var i=function(){var t=this,n=t.$createElement,e=t._self._c||n;return e("div",{staticClass:"about"},t._l(t.aboutList,(function(n,i){return e("div",{key:i,staticClass:"about-item"},[e("div",{staticClass:"title"},[t._v(t._s(n.title))]),e("div",{staticClass:"content"},[t._v(t._s(n.content))])])})),0)},s=[],a={data:function(){return{aboutList:[{title:"祖传秘方，生发秘诀",content:"《盛发康》是一家专门从事中医中药生发、黑发、生产、养发、护发、养生，集治疗、生产、销售为一体的服务型企业，公司以人为本，诚信至上，客户至上，服务至上，疗效至上，价值致尚的经营理念，在行业内享有很高的赞誉，深受消费者信赖……"},{title:"祖传秘方，传承彌新",content:"《盛发康》是川西绵竹中医世家，罗氏《盛发康》第三代生发、黑发、养发传入罗碧女士创建，1998年罗碧女士凭借祖传《生发养发秘方》在经过身为名中医的父亲罗天云精心指导下在绵竹创办了自家的养发馆，曾为数万的脱发、斑秃、白发、头屑病等患者带来治愈福音…师承有源，代代相传，罗氏《盛发康》生发养发护发传人杏林修德，仁泽四方……"},{title:"祖师简介",content:"恩师于十九世纪九十年代初出生于中医世家，三十年代毕业于华西医科大学中医专业，继开中医馆济世。五十年代初受聘于华西医大，从事中医临床学和科研工作，任中医科研室主任教授，博士生导师，四川省名老中医。《四川中医华法》审稿，定稿人。曾发表数十篇科研学术论文及专著《公证集方》等"},{title:"祖师结承护发养生和秘方，古法秘制",content:"发生于头 头为诸阳之汇，即所有阳脉经络皆聚于此，使头脑得到阳气温煦，,脑为心之舍，心主神明，是高级中枢神经系统，统管人体全部生理机能，如五脏六腑，七经八脉等，五脏之血，六腑之气均由脑调节。心主血，发乃血之余，肾之华，发其华于肾，在脑的统一协调下,百脉相通，脏腑和达,肝,肾（肝，肾同源）气血充足，滋养发肤、而百病不生，此洗剂能补肝、肾益气和血，祛风止痒，祛头屑且能生发，乌发，不产生任何副作用。该洗剂是由珍贵的纯天然植物的中草药为原料熬制而成，保留100%的药物活性成份，不含任何防腐剂和添加剂，不含氨基硅油，因氨基硅油能包住头定和头发，填满毛磷片缝隙，头发失去弹性，毛囊被填塞而失去正常代谢，则产生头屑，头痒 ,头油致头发缺乏营养而干枯，损坏，脱落。"},{title:"适用人群",content:"脂溢性脱发 ,雌性激素脱发，肾虚脱发，头发稀少，白发,产后脱发，和癌症放化疗脱发。"}]}}},c=a,o=(e("6b81"),e("5511")),u=Object(o["a"])(c,i,s,!1,null,"4ef93586",null);n["default"]=u.exports},"61ed":function(t,n,e){},"6b81":function(t,n,e){"use strict";var i=e("61ed"),s=e.n(i);s.a}}]);
