(window["webpackJsonp"]=window["webpackJsonp"]||[]).push([["about"],{"1a54":function(t,n,e){"use strict";var i=e("6961"),a=e.n(i);a.a},"613f":function(t,n,e){"use strict";e.r(n);var i=function(){var t=this,n=t.$createElement,e=t._self._c||n;return e("div",{staticClass:"about"},t._l(t.aboutList,(function(n,i){return e("div",{key:i,staticClass:"about-item"},[n.title?e("div",{staticClass:"title"},[t._v(t._s(n.title))]):t._e(),n.content?e("div",{staticClass:"content"},[t._v(t._s(n.content))]):t._e()])})),0)},a=[],s={data:function(){return{aboutList:[{title:"",content:"“盛发康生发乌发养护中心”是一 家专业防脱生发.理疗养发、生物科研的现代连锁企业。公司以中医学、经络学、药理学理论为基础，经过多年的反复临床实践，研究出一系列成熟、专业、高效的黑发养发的解决方案。"},{title:"",content:"发展史:盛发康创始人罗碧传承中医精髓。"},{title:"",content:"祖师于上世纪九十年代初出生于中医世家，三十年代毕业于华西医科大学中医专业，继开中医馆济世。五十年代初受聘于华西医大，从事中医临床学和科研工作，任中医科研室主任教授，博士生导师，四川省名老中医。《四川中医华法》审稿，定稿人。曾发表数十篇科研学术论文及专著《公证集方》等，罗碧女士经德阳市中西主治医师，著名中医专家兰明寿老先生发扬光大。兰明寿先生于1966年毕业华西医科大学，并得其祖师真传，是著名中医专 家。从医50多年，在国内外发表学术论文23篇，5项获得科研成果奖，并担任《中国特色医疗》副总编。积累了丰富的临床经验，对斑秃、脱发、面部雀斑、青春痘、肾虚、肾病、肝病、胃病、骨质增生、椎间盘突出、乳腺小叶增生、及妇科、儿科、痔瘘科等疑难杂症的症断、 治疗有独特疗效。经过研究、实验、改良、再实验、再改良，反反复复无数次的改进后，发明了纯中药独家配方，并自开门店，效果显著，很多人在多处求助无果的情况下在这里找到了满意的答案。如今门店生意红火、口口相传， 但我们并没有停下继续改进的脚步，今天研制出的养发产品不仅效果好，而且治疗时间短，一般脱发一 周即可见到效果，大家都说真是神了！更让人放心的是产品为几十种纯中药精制而成，安全无任何毒副作用，被称为可以喝的药水！"}]}}},c=s,o=(e("1a54"),e("2877")),u=Object(o["a"])(c,i,a,!1,null,"0ae4284f",null);n["default"]=u.exports},6961:function(t,n,e){}}]);
